INSERT INTO characters (name, role, backstory, objectives, secrets, relationships, clues) VALUES
('Marlowe the Devout', 'Cult Leader', 'High Priest(ess) of the Shadowmoor cult, dedicated to maintaining the rituals that keep the Eldritch Whisperer at bay.', 'Maintain order and ensure the ritual proceeds.', 'Suspects the Sacrifice had doubts about the ritual.', 'Leader of the cult, connected to all cult members.', 'Knows about the Sacrifice''s wavering commitment to the ritual.'),

('Thalia the Ambitious', 'Cultist', 'An eager acolyte determined to rise within the cult ranks.', 'Rise in rank by finding the murderer.', 'Had a secret meeting with the Sacrifice before their death.', 'Acolyte to Marlowe, has ambitions that others might find suspicious.', 'A note found in the Sacrifice''s belongings hints at a meeting with Thalia.'),

('Garrick the Protector', 'Cultist', 'The enforcer of the cult, tasked with keeping peace and order.', 'Protect the cult''s secrets and maintain peace.', 'Had a dispute with the Sacrifice over the ritual.', 'Close to Marlowe, wary of Thalia''s ambitions.', 'Witnessed arguing with the Sacrifice about the ritual.'),

('Elara the Visionary', 'Cultist', 'A seer with the ability to interpret visions of the future.', 'Interpret visions to prevent the Whisperer''s awakening.', 'Foretold a death but kept it secret.', 'Confidant to Marlowe, mistrustful of outsiders.', 'Had a vision of a death but did not reveal it.'),

('Bram the Knowledgeable', 'Villager', 'The village librarian, deeply familiar with ancient texts.', 'Discover an alternate ritual to prevent the Whisperer''s rise.', 'Found ancient texts suggesting a fairy''s involvement.', 'Respected by villagers, has a hidden alliance with Elara.', 'Knows about texts implicating a fairy in the ritual.'),

('Lila the Stranger', 'Villager', 'An outsider who recently arrived in Shadowmoor, seeking to uncover its secrets.', 'Uncover the village''s secrets for personal gain.', 'Knows the Sacrifice wanted to leave the village.', 'Seen as an intruder by cultists, befriended by Bram.', 'Overheard Sacrifice expressing doubts about the ritual.'),

('Finn the Inventive', 'Villager', 'The village alchemist, skilled in creating potions and concoctions.', 'Determine the poison used in the murder.', 'Found traces of poison in the Sacrifice''s belongings.', 'Works closely with Rowan, has a professional rivalry with Bram.', 'Analyzed the poison used in the Sacrifice''s death.'),

('Rowan the Tracker', 'Villager', 'A skilled hunter, familiar with the movements of creatures in the forest.', 'Uncover unusual forest movements.', 'Saw a fairy near the village before the murder.', 'Allied with Finn, suspicious of forest fairies.', 'Tracked movements of a fairy near the village.'),

('Hugo the Strong', 'Villager', 'The village blacksmith, known for his strength and reliability.', 'Protect the village and find the murderer.', 'Had a heated argument with the Sacrifice the night before.', 'Respected by villagers, seen as a protector.', 'Seen arguing with the Sacrifice over village matters.'),

('Evelyn the Phantom', 'Supernatural', 'A ghost that haunts the village, delivering cryptic warnings.', 'Deliver cryptic warnings and aid in finding the murderer.', 'Knows the Sacrifice''s true fears.', 'Feared by some villagers, a guide to others.', 'Speaks in riddles about the Sacrifice''s last moments.'),

('Celine the Creative', 'Villager', 'An artist who draws inspiration from the mystical forest.', 'Distract and entertain while uncovering clues.', 'Painted a scene that hints at the murderer''s identity.', 'Friend to many villagers, inspires curiosity and wonder.', 'Artwork depicts a scene that reveals clues about the murder.'),

('Alaric the Wise', 'Villager', 'The village elder, revered for his wisdom and guidance.', 'Guide the villagers and uncover the truth.', 'Knows about the Sacrifice''s doubts regarding the ritual.', 'Trusted advisor to many, including Marlowe.', 'Aware of Sacrifice''s hesitation about the ritual.'),

('Myra the Mystic', 'Cultist', 'A runemaster with knowledge of ancient symbols and their power.', 'Decipher runes to guide the ritual.', 'Suspects the runes were tampered with.', 'Respected by Marlowe, distrusted by outsiders.', 'Runes suggest tampering that implicates a villager.'),

('Silas the Elusive', 'Villager', 'A mysterious figure who knows more than he lets on.', 'Manipulate others using secrets.', 'Knows the last person who saw the Sacrifice alive.', 'Keeps his distance, but has key information.', 'Witnessed Sacrifice''s final moments with Thalia.'),

('Sylvara the Whisperer', 'Supernatural', 'A forest fairy dedicated to protecting the ancient woods.', 'Protect the forest and aid in uncovering the truth.', 'Knows the Whisperer''s true nature.', 'Allied with Rowan, distrusted by some villagers.', 'Holds secrets about the forest''s connection to the murder.'),

('Lioren the Mischievous', 'Supernatural', 'A trickster fairy who enjoys causing chaos.', 'Create chaos and enjoy the mystery.', 'Knows the murderer but enjoys the game.', 'Enjoys playing with both villagers and cultists.', 'Provides misleading clues for fun but knows the truth.');